INSERT INTO `wp_posts` VALUES (319,1,'2024-10-09 19:55:19','2024-10-09 17:55:19','/**\r\n * Custom shortcode to display WPForms form entries in table view.\r\n *\r\n * Basic usage: [wpforms_entries_table id=\"FORMID\"].\r\n * \r\n * Possible shortcode attributes:\r\n * id (required)  Form ID of which to show entries.\r\n * user           User ID, or \"current\" to default to current logged in user.\r\n * fields         Comma separated list of form field IDs.\r\n * number         Number of entries to show, defaults to 30.\r\n * \r\n * @link https://wpforms.com/developers/how-to-display-form-entries/\r\n *\r\n * Realtime counts could be delayed due to any caching setup on the site\r\n *\r\n * @param array $atts Shortcode attributes.\r\n * \r\n * @return string\r\n */\r\n  \r\nfunction wpf_entries_table( $atts ) {\r\n  \r\n    // Pull ID shortcode attributes.\r\n    $atts = shortcode_atts(\r\n        [\r\n            \'id\'     => \'\',\r\n            \'user\'   => \'\',\r\n            \'fields\' => \'\',\r\n            \'number\' => \'\',\r\n                        \'type\' => \'all\' // all, unread, read, or starred.\r\n        ],\r\n        $atts\r\n    );\r\n  \r\n    // Check for an ID attribute (required) and that WPForms is in fact\r\n    // installed and activated.\r\n    if ( empty( $atts[\'id\'] ) || ! function_exists( \'wpforms\' ) ) {\r\n        return;\r\n    }\r\n  \r\n    // Get the form, from the ID provided in the shortcode.\r\n    $form = wpforms()->form->get( absint( $atts[\'id\'] ) );\r\n  \r\n    // If the form doesn\'t exists, abort.\r\n    if ( empty( $form ) ) {\r\n        return;\r\n    }\r\n  \r\n    // Pull and format the form data out of the form object.\r\n    $form_data = ! empty( $form->post_content ) ? wpforms_decode( $form->post_content ) : \'\';\r\n  \r\n    // Check to see if we are showing all allowed fields, or only specific ones.\r\n    $form_field_ids = isset( $atts[\'fields\'] ) && $atts[\'fields\'] !== \'\' ? explode( \',\', str_replace( \' \', \'\', $atts[\'fields\'] ) ) : [];\r\n  \r\n    // Setup the form fields.\r\n    if ( empty( $form_field_ids ) ) {\r\n        $form_fields = $form_data[\'fields\'];\r\n    } else {\r\n        $form_fields = [];\r\n        foreach ( $form_field_ids as $field_id ) {\r\n            if ( isset( $form_data[\'fields\'][ $field_id ] ) ) {\r\n                $form_fields[ $field_id ] = $form_data[\'fields\'][ $field_id ];\r\n            }\r\n        }\r\n    }\r\n  \r\n    if ( empty( $form_fields ) ) {\r\n        return;\r\n    }\r\n  \r\n    // Here we define what the types of form fields we do NOT want to include,\r\n    // instead they should be ignored entirely.\r\n    $form_fields_disallow = apply_filters( \'wpforms_frontend_entries_table_disallow\', [ \'divider\', \'html\', \'pagebreak\', \'captcha\' ] );\r\n  \r\n    // Loop through all form fields and remove any field types not allowed.\r\n    foreach ( $form_fields as $field_id => $form_field ) {\r\n        if ( in_array( $form_field[\'type\'], $form_fields_disallow, true ) ) {\r\n            unset( $form_fields[ $field_id ] );\r\n        }\r\n    }\r\n  \r\n    $entries_args = [\r\n        \'form_id\' => absint( $atts[\'id\'] ),\r\n    ];\r\n  \r\n    // Narrow entries by user if user_id shortcode attribute was used.\r\n    if ( ! empty( $atts[\'user\'] ) ) {\r\n        if ( $atts[\'user\'] === \'current\' && is_user_logged_in() ) {\r\n            $entries_args[\'user_id\'] = get_current_user_id();\r\n        } else {\r\n            $entries_args[\'user_id\'] = absint( $atts[\'user\'] );\r\n        }\r\n    }\r\n  \r\n    // Number of entries to show. If empty, defaults to 30.\r\n    if ( ! empty( $atts[\'number\'] ) ) {\r\n        $entries_args[\'number\'] = absint( $atts[\'number\'] );\r\n    }\r\n  \r\n// Filter the type of entries all, unread, read, or starred\r\n    if ( $atts[\'type\'] === \'unread\' ) {\r\n        $entries_args[\'viewed\'] = \'0\';\r\n    } elseif( $atts[\'type\'] === \'read\' ) {\r\n        $entries_args[\'viewed\'] = \'1\';\r\n    } elseif ( $atts[\'type\'] === \'starred\' ) {\r\n        $entries_args[\'starred\'] = \'1\';\r\n    }\r\n  \r\n    // Get all entries for the form, according to arguments defined.\r\n    // There are many options available to query entries. To see more, check out\r\n    // the get_entries() function inside class-entry.php (https://a.cl.ly/bLuGnkGx).\r\n    $entries = wpforms()->entry->get_entries( $entries_args );\r\n  \r\n    if ( empty( $entries ) ) {\r\n        return \'<p>No entries found.</p>\';\r\n    }\r\n  \r\n    ob_start();\r\n  \r\n    echo \'<table class=\"wpforms-frontend-entries\">\';\r\n  \r\n        echo \'<thead><tr>\';\r\n  \r\n            // Loop through the form data so we can output form field names in\r\n            // the table header.\r\n            foreach ( $form_fields as $form_field ) {\r\n  \r\n                // Output the form field name/label.\r\n                echo \'<th>\';\r\n                    echo esc_html( sanitize_text_field( $form_field[\'label\'] ) );\r\n                echo \'</th>\';\r\n            }\r\n  \r\n        echo \'</tr></thead>\';\r\n  \r\n        echo \'<tbody>\';\r\n  \r\n            // Now, loop through all the form entries.\r\n            foreach ( $entries as $entry ) {\r\n  \r\n                echo \'<tr>\';\r\n  \r\n                // Entry field values are in JSON, so we need to decode.\r\n                $entry_fields = json_decode( $entry->fields, true );\r\n  \r\n                foreach ( $form_fields as $form_field ) {\r\n  \r\n                    echo \'<td>\';\r\n  \r\n                        foreach ( $entry_fields as $entry_field ) {\r\n                            if ( absint( $entry_field[\'id\'] ) === absint( $form_field[\'id\'] ) ) {\r\n                                echo apply_filters( \'wpforms_html_field_value\', wp_strip_all_tags( $entry_field[\'value\'] ), $entry_field, $form_data, \'entry-frontend-table\' );\r\n                                break;\r\n                            }\r\n                        }\r\n  \r\n                    echo \'</td>\';\r\n                }\r\n  \r\n                echo \'</tr>\';\r\n            }\r\n  \r\n        echo \'</tbody>\';\r\n  \r\n    echo \'</table>\';\r\n  \r\n    $output = ob_get_clean();\r\n  \r\n    return $output;\r\n}\r\nadd_shortcode( \'wpforms_entries_table\', \'wpf_entries_table\' );','WP-View Forms Entries','','trash','closed','closed','','wp-view-forms-entries__trashed','','','2024-10-10 06:07:54','2024-10-10 04:07:54','',0,'http://nation-sound77.local/?post_type=wpcode&#038;p=319',0,'wpcode','',0);
